mapper.select:
SELECT PAIS.C_CODPAIS AS P_CODIGOPAIS, PAIS.T_SIGLA AS P_SIGLA, PAIS.T_ABREV AS P_ABREVIACION, PAIS.T_NOMBRE AS P_NOMBRE
FROM AN_PAIS PAIS
WHERE PAIS.C_CODPAIS = #{codigoPais}

SELECT REG.C_CODREG AS R_CODIGOREGION, REG.T_SIGLA AS R_SIGLA, REG.T_NOMBRE AS R_NOMBRE, REG.T_IDENT AS R_IDENTIFICACION, REG.C_CODPAIS AS R_CODIGOPAIS
FROM AN_REGION REG
WHERE REG.C_CODPAIS = #{codigoPais}
WHERE REG.C_CODREG = #{codigoRegion}

SELECT CIU.C_CODCIU AS C_CODIGOCIUDAD, CIU.T_SIGLA AS C_SIGLA, CIU.T_NOMBRE AS C_NOMBRE, CIU.C_CODREG AS C_CODIGOREGION
FROM AN_CIUDAD CIU
WHERE CIU.C_CODREG = #{codigoRegion}
WHERE CIU.C_CODCIU = #{codigoCiudad}

SELECT COM.C_CODCOM AS CO_CODIGOCOMUNA, COM.T_SIGLA AS CO_SIGLA, COM.T_NOMBRE AS CO_NOMBRE, COM.C_CODCIU AS CO_CODIGOCIUDAD
FROM AN_COMUNA COM
WHERE COM.C_CODCIU = #{codigoCiudad}
WHERE COM.C_CODCOM = #{codigoComuna}

SELECT 
	CA.C_TIPCATAL    AS C_CODIGO,
	CA.T_DESCRIPCION AS C_VALOR

FROM AN_CATALOGO CA

WHERE CA.C_CATALOGO = #{tipoCatalogo}


	AND CA.C_TIPCATAL = #{key}



	AND CA.T_DESCRIPCION = #{value}



	AND CA.T_VIGENTE = #{vigente}

mapper.update:
UPDATE AN_CATALOGO 
	SET T_DESCRIPCION = #{secuencia} 
WHERE C_CATALOGO = 'SECUENCIA_REGISTRO' 
AND C_TIPCATAL = #{key}
