mapper.select:
SELECT TRANS.C_CODTRAN AS T_CODIGOTRANSACCION, TRANS.C_ESTADO AS T_ESTADO, TRANS.D_FECCREA AS T_FECHACREACION, TRANS.D_FECTER AS T_FECHATERMINO, TRANS.N_MTOTOTAL AS T_NOMTOTOTAL, TRANS.N_CMPLEJ AS T_COMPLEJIDAD, TRANS.T_DESCRIPCION AS T_DESCRIPCION, TRANS.C_CODMEDPAG AS T_CODIGOMEDIOPAGO, TRANS.C_CODSUB AS T_CODIGOSUBASTA, TIPOMONEDA.C_TIPCATAL AS T_TIPOMONEDAID, TIPOMONEDA.T_DESCRIPCION AS T_TIPOMONEDADESC
FROM AN_TRANSACCION TRANS INNER JOIN AN_CATALOGO TIPOMONEDA ON TRANS.C_TIPMON = TIPOMONEDA.C_TIPCATAL
WHERE TIPOMONEDA.C_CATALOGO = 'C_TIPMON' AND TRANS.C_CODTRAN = #{codigoTransaccion}

SELECT BITTRANS.C_CODBITTRAN AS BT_CODIGOBITTRANSACCION, BITTRANS.T_ESTORI AS BT_ESTADOORIGEN, BITTRANS.T_ESTFIN AS BT_ESTADOFINAL, BITTRANS.D_FECMOV AS BT_FECHAMOVIMIENTO, BITTRANS.T_DESCRIPCION AS BT_DESCRIPCION, BITTRANS.C_CODTRAN AS BT_CODIGOTRANSACCION
FROM AN_BITTRANSACCION BITTRANS
WHERE BITTRANS.C_CODTRAN = #{codigoTransaccion} AND BITTRANS.C_CODBITTRAN = #{codigoBitTransaccion}

SELECT MEDPAG.C_CODMEDPAG AS MP_CODIGOMEDIOPAGO, MEDPAG.D_FECPAG AS MP_FECHAPAGO, MEDPAG.N_MONTO AS MP_MONTO, MEDPAG.N_CUOTAS AS MP_CUOTAS, MEDPAG.N_MTOCTA AS MP_MONTOCUOTA, MEDPAG.T_DESCRIPCION AS MP_DESCRIPCION, TIPVIAPAGO.C_TIPCATAL AS MP_TIPVIAPAGOID, TIPVIAPAGO.T_DESCRIPCION AS MP_TIPVIAPAGODESC, MEDPAG.C_CODTAR AS MP_CODIGOTARJETA
FROM AN_MEDIOPAGO MEDPAG INNER JOIN AN_CATALOGO TIPVIAPAGO ON MEDPAG.C_VIAPAG = TIPVIAPAGO.C_TIPCATAL
WHERE TIPOMONEDA.C_CATALOGO = 'C_VIAPAG' AND MEDPAG.C_CODMEDPAG = #{codigoMedioPago}

SELECT TAR.C_CODTAR AS T_CODIGOTARJETA, TAR.D_FECEXP AS T_FECHAEXPIRACION, TAR.T_NROTAR AS T_NROTARJETA, TIPTARJETA.C_TIPCATAL AS T_TIPTARJETAID, TIPTARJETA.T_DESCRIPCION AS T_TIPTARJETADESC, ENTIDAD.C_TIPCATAL AS T_ENTIDADID, ENTIDAD.T_DESCRIPCION AS T_ENTIDADDESC
FROM AN_TARJETA TAR INNER JOIN AN_CATALOGO TIPTARJETA ON TAR.C_TIPTAR = TIPTARJETA.C_TIPCATAL INNER JOIN AN_CATALOGO ENTIDAD ON TAR.C_ENTIDAD = ENTIDAD.C_TIPCATAL
WHERE TIPTARJETA.C_CATALOGO = 'C_TIPTAR' AND TIPTARJETA.C_CATALOGO = 'ENTIDAD_COMERCIAL' AND TAR.C_CODTAR = #{codigoTarjeta}

mapper.update:
UPDATE AN_TARJETA
SET T_ENTIDAD = #{entidad},
	D_FECEXP = #{fechaExpiracion},
	T_NROTAR = #{nroTarjeta},
	C_TIPTAR = #{tipoTarjeta}
WHERE C_CODTAR = #{codigoTarjeta}
